mapper.update:
UPDATE menu
SET(
	menuType = #{menuType},
	menuName = #{menuName},
	menuRealImage = #{menuRealImage},
	menuFakeImage = #{menuFakeImage}
)
WHERE menuNo = #{menuNo}

mapper.select:
SELECT(
	menuName,
)
FROM menu
WHERE cafeNo = #{cafeNumber}
AND menuType = #{menuType}

SELECT(
	menuRealImage,
	menuFakeImage
)
FROM menu
WHERE menuNo = #{menuNo}
