mapper.select:
SELECT 
	FROM(SELECT CEIL(rownum/#{itemPerPage}) page, 
		     FROM(SELECT 
				     FROM report_board WHERE reporttype= #{reportType} order by reportboardNo desc
				     )
			 )
	WHERE page = #{page}

SELECT 
	FROM(SELECT CEIL(rownum/#{itemPerPage}) page, 
		     FROM(SELECT 
				     FROM report_board WHERE memberId=#{memberId} order by reportboardNo desc
				     )
			 )
	WHERE page = #{page}
